mapper.select:
SELECT s.saving_id, s.saving_name, s.join_deny, s.join_way, b.bank_id, b.bank_name, b.bank_logo_url, b.bank_url, ir.intr_rate_id, ir.intr_rate_type, ir.save_term, ir.intr_rate, ir.intr_rate2 FROM savings_products AS s INNER JOIN banks AS b ON s.bank_id = b.bank_id LEFT JOIN saving_interest_rates AS ir ON s.saving_id = ir.saving_id WHERE s.fin_category_id = #{finCategoryId}
AND s.saving_name LIKE CONCAT('%', #{searchValue}, '%')
AND b.bank_name IN
#{name}
AND ir.save_term = #{saveTerm}
AND ir.intr_rate_type = #{interestRateType}
ORDER BY b.bank_type = 1 DESC, b.bank_type, ir.intr_rate DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM savings_products AS s
INNER JOIN banks AS b ON s.bank_id = b.bank_id
LEFT JOIN saving_interest_rates AS ir ON s.saving_id = ir.saving_id
WHERE s.fin_category_id = #{finCategoryId}


    AND s.saving_name LIKE CONCAT('%', #{searchValue}, '%')


    AND b.bank_name IN

        #{name}



    AND ir.save_term = #{saveTerm}


    AND ir.intr_rate_type = #{interestRateType}
